#define ALIGN(a) ((OFFSET(a)) ? ((a) - OFFSET(a) + sizeof(size\_t)) : (a))

struct fsa
{
 size\_t head;
};

size\_t FSARequiredPoolSize(size\_t num\_of\_blocks, size\_t block\_size)
{
 block\_size = ALIGN(block\_size);

 return (sizeof(fsa\_t) + (block\_size \* num\_of\_blocks) + sizeof(size\_t));
}

fsa\_t \*FSAInit(void \*memory\_pool, size\_t num\_of\_blocks, size\_t block\_size)
{
 fsa\_t \*fsa = NULL;
 char \*runner = NULL;
 size\_t offset = 0;
 size\_t i = 0;

 assert(memory\_pool);

 block\_size = ALIGN(block\_size); 
 fsa = (fsa\_t \*)ALIGN((size\_t)memory\_pool);

 offset = sizeof(fsa\_t);

 fsa->head = offset;
 runner = (char \*)fsa + offset;

 for (i = 0; i < num\_of\_blocks; ++i)
 {
 offset += block\_size
 \*(size\_t \*)runner = (char \*)fsa + offset;
 runner += block\_size;
 }

 \*(size\_t \*)runner = 0;

 return fsa;
}

void \*FSAAllocate(fsa\_t \*fsa)
{
 void \*result = NULL;

 assert(fsa);

 if (0 != fsa->head)
 {
 result = (char \*)fsa + fsa->head;
 fsa->head = \*(size\_t \*)result;
 }

 return result;
}

void FSAFree(fsa\_t \*fsa, void \*block)
{
 assert(fsa);
 assert(block);

 \*(size\_t \*)block = fsa->head;
 fsa->head = (char \*)block - (char \*)fsa;
}

size\_t FSACountFree(const fsa\_t \*fsa)
{
 size\_t counter = 0;
 size\_t offset = 0;

 assert(fsa);

 offset = fsa->head;

 while (0 != \*(size\_t \*)((const char \*)fsa + offset))
 {
 offset = \*(size\_t \*)((const char \*)fsa + offset);
 ++counter;
 }

 return counter;
}

