public string GetAoiGroupOrder(string shopOrder)
 {
 try
 {
 var orders = \_oracleDataRepository.GetAOIGroupOrder(shopOrder).OrderBy(o => o.GroupSel).ThenBy(o => o.Remark);

 var illegalRegex = @"[""\\,&;%]";
 var groupRegex = @"^(GROUP SEL(?:ECT)?)\s+(.+)$";
 var remarkRegex = @"([A-Z])0+([1-9A-Z]\w\*)";

 var groupOrder = orders.Select(x =>
 {
 var legalStr = Regex.IsMatch(x.GroupSel, illegalRegex)
 ? Regex.Replace(x.GroupSel, illegalRegex, " ").Trim()
 : x.GroupSel.Trim();

 var remark = Regex.Replace(x.Remark, remarkRegex, "$1$2");

 var segments = legalStr.Split(' ').Where(y => !string.IsNullOrEmpty(y)).ToArray();

 if (legalStr.Contains(remark))
 {
 var remarkIdx = Array.IndexOf(segments, remark);

 if (remarkIdx == segments.Length - 1 && !Regex.IsMatch(segments[remarkIdx - 1], @"SEL"))
 {
 legalStr = string.Join(" ", segments
 .Where((value, index) => index != remarkIdx && index != remarkIdx - 1)
 .Concat(new string[] { segments[remarkIdx], segments[remarkIdx - 1] }));
 }
 else
 {
 legalStr = string.Join(" ", segments);
 }
 }
 else
 {
 legalStr = string.Join(" ", segments);
 }

 return legalStr;
 })
 .OrderBy(sort => sort)
 .Distinct()
 .Select(x => Regex.Replace(x, groupRegex, "$2").Trim()).ToList();

