static void uart\_joycon\_write\_next\_command(uart\_joycon\_t \*joycon)
{
 uint8\_t i;
 if (joycon->handshake\_pos == 10)
 {
 uart\_handshake\_commands[joycon->handshake\_pos][25] = host\_mac\_addr[5];
 uart\_handshake\_commands[joycon->handshake\_pos][26] = host\_mac\_addr[4];
 uart\_handshake\_commands[joycon->handshake\_pos][27] = host\_mac\_addr[3];
 uart\_handshake\_commands[joycon->handshake\_pos][28] = host\_mac\_addr[2];
 uart\_handshake\_commands[joycon->handshake\_pos][29] = host\_mac\_addr[1];
 uart\_handshake\_commands[joycon->handshake\_pos][30] = host\_mac\_addr[0];
 }
 for (i = 1; i <= uart\_handshake\_commands[joycon->handshake\_pos][0]; i++)
 joycon->uart\_port->fifo.rw\_byte = uart\_handshake\_commands[joycon->handshake\_pos][i];
 if (joycon->handshake\_pos < 14)
 joycon->handshake\_pos++;
}
static void uart\_joycon\_intr\_handle(uart\_joycon\_t \*joycon)
{
 if (!joycon->chars\_remaining)
 gpio\_set\_level(joycon->rx\_en\_pin, 0);
 uart\_disable\_rx\_intr(joycon->uart\_num);
 uart\_clear\_intr\_status(joycon->uart\_num, UART\_RXFIFO\_FULL\_INT\_CLR | UART\_RXFIFO\_TOUT\_INT\_CLR);

 uint16\_t rx\_fifo\_len;
 rx\_fifo\_len = joycon->uart\_port->status.rxfifo\_cnt;

 if (!joycon->chars\_remaining)
 {
 joycon->message\_started = 1;
 joycon->message\_len = 0;
 }
 else if (rx\_fifo\_len >= joycon->chars\_remaining)
 {
 joycon->message\_started = 0; // Message ends when remaining chars are received
 joycon->chars\_remaining = 0;
 }

 // Read data
 while (rx\_fifo\_len)
 {
 joycon->rx\_buf[joycon->message\_len] = joycon->uart\_port->fifo.rw\_byte;
 joycon->message\_len++;
 rx\_fifo\_len--;
 }

 if (joycon->message\_started)
 {
 joycon->chars\_remaining = joycon->rx\_buf[3] + 5 - joycon->message\_len;
 if (joycon->chars\_remaining > 0)
 joycon->uart\_intr.rxfifo\_full\_thresh = joycon->chars\_remaining;
 else
 joycon->chars\_remaining = 8; // Assuming 8 chars remaining
 }
 else
 {
 joycon->message\_received = 1;
 joycon->uart\_intr.rxfifo\_full\_thresh = 4; // Wait for next command header
 if (!gpio\_get\_level(joycon->tx\_en\_pin))
 uart\_joycon\_write\_next\_command(joycon);
 else
 joycon->command\_queued = 1;
 if (!memcmp(joycon->rx\_buf, uart\_response\_baud\_switched, 12))
 joycon->baud\_switch\_queued = 1;
 }
 uart\_intr\_config(joycon->uart\_num, &joycon->uart\_intr);
 uart\_enable\_rx\_intr(joycon->uart\_num);

 gpio\_set\_level(joycon->rx\_en\_pin, 1);
}

static void joycon\_right\_intr\_handle(void \*arg)
{
 uart\_joycon\_intr\_handle(&joycon\_right);
}

static void joycon\_left\_intr\_handle(void \*arg)
{
 uart\_joycon\_intr\_handle(&joycon\_left);
}

static void uart\_joycon\_setup(uart\_joycon\_t \*joycon)
{
 gpio\_set\_direction(joycon->tx\_en\_pin, GPIO\_MODE\_INPUT);
 gpio\_set\_pull\_mode(joycon->tx\_en\_pin, GPIO\_PULLUP\_ONLY);
 gpio\_set\_direction(joycon->rx\_en\_pin, GPIO\_MODE\_OUTPUT);
 gpio\_set\_level(joycon->rx\_en\_pin, 1);

 joycon->uart\_config.baud\_rate = 1000000;
 joycon->uart\_config.data\_bits = UART\_DATA\_8\_BITS;
 joycon->uart\_config.parity = UART\_PARITY\_DISABLE;
 joycon->uart\_config.stop\_bits = UART\_STOP\_BITS\_2;
 joycon->uart\_config.flow\_ctrl = UART\_HW\_FLOWCTRL\_DISABLE;
 joycon->uart\_config.source\_clk = SOC\_MOD\_CLK\_APB;
 uart\_param\_config(joycon->uart\_num, &joycon->uart\_config);
 uart\_set\_pin(joycon->uart\_num, joycon->tx\_pin, joycon->rx\_pin, UART\_PIN\_NO\_CHANGE, UART\_PIN\_NO\_CHANGE);
 uart\_set\_line\_inverse(joycon->uart\_num, UART\_SIGNAL\_TXD\_INV);

 ESP\_ERROR\_CHECK(uart\_driver\_install(joycon->uart\_num, uart\_buffer\_size, uart\_buffer\_size, 10, &joycon->uart\_queue, 0));
