private IEnumerable MergeRowModels(IEnumerable currentRowModels, IEnumerable historicRowModels)
 {
 var mergedRowModels = new List();
 mergedRowModels.AddRange(currentRowModels);
 historicRowModels.ToList().ForEach(historicRowModel =>
 AddOrUpdateRowModel(mergedRowModels, historicRowModel)
 );
 return mergedRowModels;
 }
 private void AddOrUpdateRowModel(List rowModels, ConfigurationVersionManagementRowModel rowModel)
 {
 var existingRowWithSameKey = rowModels.SingleOrDefault(c => c.SettingKey == rowModel.SettingKey);
 if (existingRowWithSameKey == null)
 {
 rowModels.Add(rowModel);
 }
 else
 {
 existingRowWithSameKey.SelectedVersionSettingValueAsText = rowModel.SelectedVersionSettingValueAsText;
 }
 }

