class NetworkException extends Equatable {
 final ProfileController _profileController = Get.find();
 final GetStorage box = GetStorage();
 Failures fromDio(DioError dioError,
 [dynamic message, dynamic messageConstant]) {
 if (messageConstant != null) {
 return Failures(messageConstant);
 }
 if (dioError.response?.statusCode != null) {
 if (dioError.response!.statusCode! >= 500) {
 String? errorMessage;
 if (message != null) {
 ErrorMessage e = box.read(kErrorMessage);
 if (message is Map) {
 Map mapErrorMessage = message;
 var value = mapErrorMessage.values.first;
 errorMessage = e.data[value];
 } else {
 errorMessage = e.data[message];
 }
 }
 errorMessage = capitalizeFirst(errorMessage ?? 'Kesalahan internal')
 .replaceAll('\_', ' ');
 return Failures(errorMessage);
 } else if (dioError.response!.statusCode! >= 400) {
 if (dioError.response?.statusCode == 401) {
 _profileController.sendLogout();
 }
 String? errorMessage;
 if (message != null) {
 ErrorMessage e = box.read(kErrorMessage);
 if (message is Map) {
 Map mapErrorMessage = message;
 var key = mapErrorMessage.keys.first;
 var value = mapErrorMessage.values.first;
 switch (key) {
 case 'name':
 key = 'nama';
 break;
 case 'phone':
 key = '';
 break;
 default:
 }
 errorMessage =
 (key == 'general') ? e.data[value] : '$key ${e.data[value]}';
 } else {
 errorMessage = e.data[message];
 }
 }
 errorMessage = capitalizeFirst(errorMessage ?? 'Terjadi kesalahan')
 .replaceAll('\_', ' ');
 return Failures(errorMessage);
 }
 }
 return const Failures('Tidak ada Koneksi Internet');
 }
 String capitalizeFirst(String s) =>
 (s.length > 1) ? s[0].toUpperCase() + s.substring(1) : s.toUpperCase();
 @override
 List get props => [];
}

