import { useState } from 'react';
import _ from 'lodash';
interface Deck {
 drawPile: C[];
 discardPile: C[];
}
const useDeck = (initialCards: C[]) => {
 const [deck, setDeck] = useState>({
 drawPile: initialCards,
 discardPile: [],
 });
 const [hand, setHand] = useState([]);
 const drawHand = (numCards: number) => {
 if (deck.drawPile.length === 0) {
 setDeck({
 drawPile: _.shuffle(deck.discardPile),
 discardPile: [],
 });
 }
 const drawnCards = deck.drawPile.slice(0, numCards);
 setHand(prevHand => [...prevHand, ...drawnCards]);
 setDeck(prevDeck => ({
 drawPile: prevDeck.drawPile.slice(numCards),
 discardPile: [...prevDeck.discardPile, ...drawnCards],
 }));
 };
 const shuffle = () => {
 setDeck(prevDeck => ({
 drawPile: _.shuffle([...prevDeck.drawPile, ...prevDeck.discardPile]),
 discardPile: [],
 }));
 };
 return [hand, drawHand, shuffle];
};
export default useDeck;
