void solve() {
 int n;
 std::cin >> n;

 i64 sum = 0;
 int neg = 0;
 std::vector a(n);
 for (int i = 0; i < n; i++) {
 std::cin >> a[i];
 if (a[i] < 0) {
 neg++;
 a[i] = -a[i];
 }
 sum += a[i];
 }
 if (neg % 2 == 1) {
 int mn = \*std::min\_element(a.begin(), a.end());
 sum -= 2 \* mn;
 }
 std::cout << sum << "\n";
}

